import React, { useState, useEffect } from 'react';
import { useTable } from 'react-table';

// Main App component
function App() {
    const [data, setData] = useState([{}]);
    const [newTitle, setNewTitle] = useState('');
    const [newDesc, setNewDesc] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    useEffect(() => {
        if (loggedIn) fetchData();
    }, [loggedIn]);

    const fetchData = async () => {
        try {
            const response = await fetch('/todos');
            const result = await response.json();
            setData(result.todos);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (username === 'admin' && password === 'password') {
            setLoggedIn(true);
        } else {
            alert('Invalid credentials!');
        }
    };

    const handleAddTodo = async () => {
        if (newTitle === '' || newDesc === '') return;

        try {
            await fetch('/add_todo', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ title: newTitle, description: newDesc })
            });
            fetchData();
            setNewTitle('');
            setNewDesc('');
        } catch (error) {
            console.error('Error adding todo:', error);
        }
    };

    const handleDeleteTodo = async (deleteId) => {
        try {
            await fetch('/delete_todo', {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id: deleteId })
            });
            fetchData();
        } catch (error) {
            console.error('Error deleting todo:', error);
        }
    };

    const handleCheckTodo = async (checkId) => {
        try {
            await fetch('/check_todo', {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id: checkId })
            });
            fetchData();
        } catch (error) {
            console.error('Error checking todo:', error);
        }
    };

    const columns = React.useMemo(() => [
        { Header: 'ID', accessor: 'id' },
        { Header: 'Title', accessor: 'title' },
        { Header: 'Description', accessor: 'description' },
        {
            Header: 'Done?',
            accessor: 'done',
            Cell: ({ value }) => <td>{value}</td>,
        }
    ], []);

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({ columns, data });

    if (!loggedIn) {
        return (
            <div>
                <form onSubmit={handleLogin}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                    <button type="submit">Log In</button>
                </form>
            </div>
        );
    }

    return (
        <div>
            <div>
                <input
                    type="text"
                    placeholder="Title"
                    value={newTitle}
                    onChange={(e) => setNewTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={newDesc}
                    onChange={(e) => setNewDesc(e.target.value)}
                />
                <button onClick={handleAddTodo}>Add To-do</button>
            </div>

            <div>
                <table {...getTableProps()}>
                    <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map((column) => (
                                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                            <th>Change status to:</th>
                            <th>Delete</th>
                        </tr>
                    ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                    {rows.map((row) => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map((cell) => (
                                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                ))}
                                <td>
                                    <button onClick={() => handleCheckTodo(row.cells[0].value)}>
                                        {row.cells[3].value === 'YES' ? 'Not done' : 'Done'}
                                    </button>
                                </td>
                                <td>
                                    <button onClick={() => handleDeleteTodo(row.cells[0].value)}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
