// Importing necessary React libraries and components
import React, { useState, useEffect } from 'react';
import { useTable } from 'react-table';
import styled from 'styled-components';

// Importing image for display
import bookingImage from './booking.png';

// Styled components for better UI styling
const MainContainer = styled.div`
  padding: 20px;
  background-color: #f5f5f5;
`;

const Header = styled.header`
  padding: 20px;
  text-align: center;
`;

const BookingImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const TodoSection = styled.div`
  margin-top: 20px;
  background-color: #ffffff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const StyledTable = styled.table`
  border-collapse: collapse;
  border: 1px solid #ddd;
  width: 100%;
  margin-top: 20px;
`;

const StyledInput = styled.input`
  margin-right: 10px;
  padding: 8px;
`;

const Button = styled.button`
  margin-top: 10px;
  padding: 8px 16px;
  background-color: #2196f3;
  color: #fff;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: #0d47a1;
  }
`;

// Styled component for the login form
const LoginForm = styled.form`
  margin: auto;
  padding: 20px;
  max-width: 300px;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

// Main App component
function App() {
    // State to manage todo data, new todo inputs, and login state
    const [data, setData] = useState([{}]);
    const [newTitle, setNewTitle] = useState('');
    const [newDesc, setNewDesc] = useState('');
    const [loggedIn, setLoggedIn] = useState(false); // New state for login

    // New state for login credentials
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    // Function to fetch todo data from the server
    const fetchData = () => {
        fetch('/todos')
            .then((res) => res.json())
            .then((result) => {
                setData(result.todos);
            })
            .catch((error) => console.error('Error fetching data:', error));
    };

    // useEffect to fetch data when the component mounts
    useEffect(() => {
        fetchData();
    }, []);

    // Function to simulate a login process
    const handleLogin = (e) => {
        e.preventDefault();
        // Simple condition to simulate login - in a real app, you'd validate against a backend
        if (username === 'admin' && password === 'password') {
            setLoggedIn(true);
        } else {
            alert('Invalid credentials!');
        }
    };

    if (!loggedIn) {
        return (
            <MainContainer>
                <LoginForm onSubmit={handleLogin}>
                    <h2>Login</h2>
                    <StyledInput
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <StyledInput
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <Button type="submit">Log In</Button>
                </LoginForm>
            </MainContainer>
        );
    }

    // Function to handle adding a new todo based on id
    const handleAddTodo = () => {
        if (newTitle === '' || newDesc === '') {
            return;
        }
        fetch('/add_todo', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ title: newTitle, description: newDesc }),
        })
            .then((res) => res.json())
            .then(() => {
                fetchData();
            })
            .catch((error) => console.error('Error adding todo:', error));

        setNewDesc('');
        setNewTitle('');
    };

    // Function to handle deleting a todo based on id
    const handleDeleteTodo = (deleteId) => {
        fetch('/delete_todo', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id: deleteId }),
        })
            .then((res) => res.json())
            .then(() => {
                fetchData();
            })
            .catch((error) => console.error('Error deleting todo:', error));
    };

    // Function to handle checking/unchecking a todo (changes done status from YES to NO and vice versa)
    const handleCheckTodo = (checkId) => {
        fetch('/check_todo', {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id: checkId }),
        })
            .then((res) => res.json())
            .then(() => {
                fetchData();
            })
            .catch((error) => console.error('Error checking todo:', error));
    };

    // Memoized columns for the todo table
    const columns = React.useMemo(
        () => [
            { Header: 'ID', accessor: 'id' },
            { Header: 'Title', accessor: 'title' },
            { Header: 'Description', accessor: 'description' },
            {
                Header: 'Done?',
                accessor: 'done',
                Cell: ({ value }) => <DoneCell isDone={value}>{value}</DoneCell>,
            },
        ],
        []
    );

    // Styled component for rendering the 'Done' cell
    const DoneCell = styled.td`
      display: flex;
      justify-content: center;
      color: ${(props) => (props.isDone === 'YES' ? '#4CAF50' : '#FF5733')};
      font-weight: bold;
      padding: 8px;
      text-align: center;
    `;

    // Destructuring properties from useTable hook
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({ columns, data });

    // Styling for the booking logo image
    const BookingImage = ({ src, alt, style }) => (
        <img
            src={src}
            alt={alt}
            style={{
                padding: '20px',
                display: 'block',
                marginLeft: 'auto',
                marginRight: 'auto',
                ...style,
            }}
        />
    );

    // Styling for header with title
    const Header = ({ children }) => (
        <header
            style={{
                background: '#003580',
                padding: '5px',
                textAlign: 'center',
            }}
        >
            {children}
        </header>
    );

    // Rendering components and UI
    return (
        <MainContainer>
            {/* Header section with title */}
            <Header>
                <h1 style={{ color: '#ffffff', fontSize: '50px' }}>B.done</h1>
            </Header>

            {/* Section for adding a new To-do Item */}
            <TodoSection>
                <h2>Add a To-do Item</h2>
                {/* Input field for the new To-do title */}
                <StyledInput
                    type="text"
                    placeholder="Title"
                    value={newTitle}
                    onChange={(e) => setNewTitle(e.target.value)}
                />
                {/* Input field for the new To-do description */}
                <StyledInput
                    type="text"
                    placeholder="Description"
                    value={newDesc}
                    onChange={(e) => setNewDesc(e.target.value)}
                />
                {/* Button to add the new To-do */}
                <Button onClick={handleAddTodo}>Add To-do</Button>
            </TodoSection>

            {/* Section for displaying the To-do Table */}
            <TodoSection>
                {/* Styling for the To-do Table */}
                <StyledTable {...getTableProps()} style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
                    {/* Table header with dynamic column names */}
                    <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()} style={{ borderBottom: '2px solid #333' }}>
                            {headerGroup.headers.map((column) => (
                                <th
                                    {...column.getHeaderProps()}
                                    style={{
                                        padding: '12px',
                                        textAlign: 'center',
                                        borderBottom: '2px solid #ddd',
                                        background: '#f2f2f2',
                                        color: '#333',
                                    }}
                                >
                                    {column.render('Header')}
                                </th>
                            ))}
                            {/* Additional headers for status change and delete button columns */}
                            <th style={{ padding: '12px', textAlign: 'center', borderBottom: '2px solid #ddd', background: '#f2f2f2', color: '#333' }}>
                                Change status to:
                            </th>
                            <th style={{ padding: '12px', textAlign: 'center', borderBottom: '2px solid #ddd', background: '#f2f2f2', color: '#333' }}>
                                Delete
                            </th>
                        </tr>
                    ))}
                    </thead>
                    {/* Table body with To-do data */}
                    <tbody {...getTableBodyProps()}>
                    {rows.map((row) => {
                        // Prepare the row for rendering
                        prepareRow(row);
                        return (
                            <tr
                                {...row.getRowProps()}
                                style={{
                                    padding: '8px',
                                    textAlign: 'center',
                                    border: '1px solid #ddd',
                                }}
                            >
                                {/* Render individual cells within the row */}
                                {row.cells.map((cell) => {
                                    // Special styling for 'Done' cell
                                    if (cell.column.id === 'done') {
                                        return (
                                            <DoneCell
                                                {...cell.getCellProps()}
                                                isDone={cell.value}
                                                style={{
                                                    padding: '8px',
                                                    textAlign: 'center',
                                                    border: '1px solid #ddd',
                                                }}
                                            >
                                                {cell.render('Cell')}
                                            </DoneCell>
                                        );
                                    }
                                    // Regular styling for other cells
                                    return (
                                        <td
                                            {...cell.getCellProps()}
                                            style={{
                                                padding: '8px',
                                                textAlign: 'center',
                                                border: '1px solid #ddd',
                                            }}
                                        >
                                            {cell.render('Cell')}
                                        </td>
                                    );
                                })}
                                {/* Buttons for status change and delete */}
                                <td style={{ padding: '8px', textAlign: 'center', border: '1px solid #ddd' }}>
                                    <button
                                        onClick={() => handleCheckTodo(row.cells[0].value)}
                                        style={{
                                            background: row.cells[3].value === 'YES' ? '#ff6666' : '#66cc66',
                                            color: '#fff',
                                            padding: '8px 12px',
                                            border: 'none',
                                            cursor: 'pointer',
                                            borderRadius: '5px'
                                        }}
                                    >
                                        {row.cells[3].value === 'YES' ? 'Not done' : 'Done'}
                                    </button>
                                </td>
                                <td style={{ padding: '8px', textAlign: 'center', border: '1px solid #ddd' }}>
                                    <button
                                        onClick={() => handleDeleteTodo(row.cells[0].value)}
                                        style={{
                                            background: '#cc0000',
                                            color: '#fff',
                                            padding: '8px 12px',
                                            border: 'none',
                                            cursor: 'pointer',
                                            borderRadius: '5px'
                                        }}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                    </tbody>
                </StyledTable>
            </TodoSection>

            {/* Display the booking image */}
            <BookingImage src={bookingImage} alt="Booking" style={{ maxWidth: '100%', maxHeight: '100%', }} />
        </MainContainer>

    );
}

export default App;
