import React, { useState, useEffect } from 'react';
import { useTable } from 'react-table';
import styled from 'styled-components';
import bookingImage from './booking.png';

// Styled components
const MainContainer = styled.div`
  padding: 20px;
  background-color: #f5f5f5;
`;

const StyledHeader = styled.header`
  padding: 20px;
  text-align: center;
  background: #003580;
  color: #ffffff;
`;

const StyledBookingImage = styled.img`
  max-width: 100%;
  height: auto;
  padding: 20px;
  display: block;
  margin-left: auto;
  margin-right: auto;
`;

const TodoSection = styled.div`
  margin-top: 20px;
  background-color: #ffffff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const StyledTable = styled.table`
  border-collapse: collapse;
  border: 1px solid #ddd;
  width: 100%;
  margin-top: 20px;
`;

const StyledInput = styled.input`
  margin-right: 10px;
  padding: 8px;
`;

const Button = styled.button`
  margin-top: 10px;
  padding: 8px 16px;
  background-color: #2196f3;
  color: #fff;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #0d47a1;
  }
`;

const LoginForm = styled.form`
  margin: auto;
  padding: 20px;
  max-width: 300px;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const DoneCell = styled.td`
  display: flex;
  justify-content: center;
  color: ${props => props.isDone === 'YES' ? '#4CAF50' : '#FF5733'};
  font-weight: bold;
  padding: 8px;
  text-align: center;
`;

// Main App component
function App2() {
    const [data, setData] = useState([{}]);
    const [newTitle, setNewTitle] = useState('');
    const [newDesc, setNewDesc] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    useEffect(() => {
        if (loggedIn) fetchData();
    }, [loggedIn]);

    const fetchData = async () => {
        try {
            const response = await fetch('/todos');
            const result = await response.json();
            setData(result.todos);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (username === 'admin' && password === 'password') {
            setLoggedIn(true);
        } else {
            alert('Invalid credentials!');
        }
    };

    const handleAddTodo = async () => {
        if (newTitle === '' || newDesc === '') return;

        try {
            await fetch('/add_todo', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ title: newTitle, description: newDesc })
            });
            fetchData();
            setNewTitle('');
            setNewDesc('');
        } catch (error) {
            console.error('Error adding todo:', error);
        }
    };

    const handleDeleteTodo = async (deleteId) => {
        try {
            await fetch('/delete_todo', {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id: deleteId })
            });
            fetchData();
        } catch (error) {
            console.error('Error deleting todo:', error);
        }
    };

    const handleCheckTodo = async (checkId) => {
        try {
            await fetch('/check_todo', {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id: checkId })
            });
            fetchData();
        } catch (error) {
            console.error('Error checking todo:', error);
        }
    };

    const columns = React.useMemo(() => [
        { Header: 'ID', accessor: 'id' },
        { Header: 'Title', accessor: 'title' },
        { Header: 'Description', accessor: 'description' },
        {
            Header: 'Done?',
            accessor: 'done',
            Cell: ({ value }) => <DoneCell isDone={value}>{value}</DoneCell>,
        }
    ], []);

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({ columns, data });

    if (!loggedIn) {
        return (
            <MainContainer>
                <LoginForm onSubmit={handleLogin}>
                    <h2>Login</h2>
                    <StyledInput
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                    />
                    <StyledInput
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                    <Button type="submit">Log In</Button>
                </LoginForm>
            </MainContainer>
        );
    }

    return (
        <MainContainer>
            <StyledHeader><h1>B.done</h1></StyledHeader>
            {/* Todo Section, Table, and other components */}
            <StyledBookingImage src={bookingImage} alt="Booking" />
        </MainContainer>
    );
}

export default App2;
