import React, { useState, useEffect } from 'react';
import { useTable } from 'react-table';

// Main App component
function App() {
    const [data, setData] = useState([]);
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');


    useEffect(() => {
        if (loggedIn) fetchData();
    }, [loggedIn]);

    const fetchData = async () => {
        try {
            const response = await fetch(`/get_files?username=${username}`);
            const result = await response.json();
            console.log(result.files); // Check the structure here
            setData(result.files);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };


    const handleLogin = async (e) => {
        e.preventDefault();

        // Construct the login request payload
        const loginPayload = {
            username: username,
            password: password,
        };

        try {
            // Sending the login request to the backend
            const response = await fetch('/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginPayload)
            });

            // Converting the response to JSON
            const result = await response.json();

            // If login is successful, change the loggedIn state to true
            if (response.ok) {
                setLoggedIn(true);
            } else {
                // If login fails, display the error message from the server
                alert(result.message);
            }
        } catch (error) {
            console.error('Login failed:', error);
            alert('Login failed due to a network or server error.');
        }
    };

    const handleDownloadFile = (downloadId) => {
        // Construct the URL to the file based on downloadId
        const fileName = `${downloadId}.txt`;
        const fileUrl = `${process.env.PUBLIC_URL}/files/${fileName}`;

        // Create an anchor element and trigger the download
        const link = document.createElement('a');
        link.href = fileUrl;
        link.setAttribute('download', fileName); // Suggests the filename to save as
        document.body.appendChild(link); // Append to document to make 'click' work
        link.click();
        document.body.removeChild(link); // Clean up
    };

    const columns = React.useMemo(() => [
        { Header: 'ID', accessor: 'id' },
        { Header: 'user', accessor: 'user' },
        { Header: 'Description', accessor: 'description' },
    ], []);

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({
        columns,
        data: data || [] // Ensure data is always an array
    });


    if (!loggedIn) {
        return (
            <div>
                <form onSubmit={handleLogin}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                    <button type="submit">Log In</button>
                </form>
            </div>
        );
    }

    return (
        <div>
            <div>
                <table {...getTableProps()}>
                    <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map((column) => (
                                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                            <th>Download</th>
                        </tr>
                    ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                    {rows.map((row) => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map((cell) => (
                                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                ))}
                                <td>
                                    <button onClick={() => handleDownloadFile(row.cells[0].value)}>
                                        Download
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
