import React, { useState, useEffect } from 'react';
import { useTable } from 'react-table';

// Main App component
function App() {
    const [data, setData] = useState([]);
    const [newUser, setNewUser] = useState('');
    const [newDesc, setNewDesc] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');


    useEffect(() => {
        if (loggedIn) fetchData();
    }, [loggedIn]);

    const fetchData = async () => {
        try {
            const response = await fetch(`/get_files?username=${username}`);
            const result = await response.json();
            console.log(result.files); // Check the structure here
            setData(result.files);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };


    const handleLogin = async (e) => {
        e.preventDefault();

        // Construct the login request payload
        const loginPayload = {
            username: username,
            password: password,
        };

        try {
            // Sending the login request to the backend
            const response = await fetch('/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginPayload)
            });

            // Converting the response to JSON
            const result = await response.json();

            // If login is successful, change the loggedIn state to true
            if (response.ok) {
                setLoggedIn(true);
            } else {
                // If login fails, display the error message from the server
                alert(result.message);
            }
        } catch (error) {
            console.error('Login failed:', error);
            alert('Login failed due to a network or server error.');
        }
    };

    const handleAddFile = async () => {
        if (newUser === '' || newDesc === '') return;

        try {
            await fetch('/add_file', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ user: newUser, description: newDesc })
            });
            fetchData();
            setNewUser('');
            setNewDesc('');
        } catch (error) {
            console.error('Error adding file:', error);
        }
    };

    const handleDeleteFile = async (deleteId) => {
        try {
            await fetch('/delete_file', {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id: deleteId })
            });
            fetchData();
        } catch (error) {
            console.error('Error deleting file:', error);
        }
    };

    const handleCheckFile = async (checkId) => {
        try {
            await fetch('/check_file', {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ id: checkId })
            });
            fetchData();
        } catch (error) {
            console.error('Error checking file:', error);
        }
    };

    const columns = React.useMemo(() => [
        { Header: 'ID', accessor: 'id' },
        { Header: 'user', accessor: 'user' },
        { Header: 'Description', accessor: 'description' },
        {
            Header: 'Done?',
            accessor: 'done',
            Cell: ({ value }) => <td>{value}</td>,
        }
    ], []);

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({ columns, data });

    if (!loggedIn) {
        return (
            <div>
                <form onSubmit={handleLogin}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                    <button type="submit">Log In</button>
                </form>
            </div>
        );
    }

    return (
        <div>
            <div>
                <input
                    type="text"
                    placeholder="user"
                    value={newUser}
                    onChange={(e) => setNewUser(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={newDesc}
                    onChange={(e) => setNewDesc(e.target.value)}
                />
                <button onClick={handleAddFile}>Add To-do</button>
            </div>

            <div>
                <table {...getTableProps()}>
                    <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map((column) => (
                                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                            <th>Change status to:</th>
                            <th>Delete</th>
                        </tr>
                    ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                    {rows.map((row) => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map((cell) => (
                                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                ))}
                                <td>
                                    <button onClick={() => handleCheckFile(row.cells[0].value)}>
                                        {row.cells[3].value === 'YES' ? 'Not done' : 'Done'}
                                    </button>
                                </td>
                                <td>
                                    <button onClick={() => handleDeleteFile(row.cells[0].value)}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
